BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "emp" (
	"empno"	integer NOT NULL,
	"ename"	varchar,
	"job"	varchar,
	"mgr"	integer NOT NULL,
	PRIMARY KEY("empno")
);
CREATE TABLE IF NOT EXISTS "food" (
	"id"	integer NOT NULL,
	"address"	varchar,
	"name"	varchar,
	"desk"	varchar,
	"tel"	varchar,
	"latitude"	varchar,
	"longitude"	varchar,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "tour" (
	"id"	INTEGER,
	"name"	TEXT,
	"address"	TEXT,
	"desc"	TEXT,
	"simpleDesc"	TEXT,
	"tel"	TEXT,
	"code"	TEXT,
	"codeName"	TEXT,
	"mainImg"	TEXT,
	"homepage"	TEXT,
	"mapInfo"	TEXT,
	"latitude"	TEXT,
	"longitude"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "shop" (
	"id"	INTEGER,
	"name"	TEXT,
	"address"	TEXT,
	"info"	TEXT,
	"tel"	TEXT,
	"code"	TEXT,
	"codeName"	TEXT,
	"mainImg"	TEXT,
	PRIMARY KEY("id")
);
COMMIT;
